package operators;

public class Arithmetic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int a=20,b=10;
 System.out.println("The value of a+b is " +(a+b));
 System.out.println("The value of a-b is " +(a-b));
 System.out.println("The value of a*b is " +(a*b));
 System.out.println("The value of a/b is " +(a/b));
 System.out.println("The value of a%b is " +(a%b));
	}

}
